package com.core;

public class controlstmtns {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=20;
		int b=40;
		int c=60;
		if((a>b)&&(a>c))
		{
			System.out.println("a is big");
			
		}
		else if(b>c)
		{ 
			System.out.println("b is big");
			
		}
		else
		{
			System.out.println("c is big");
		}
	}

}
